mapper.insert:
INSERT INTO
		user
    SET
		email = #{email}
	,	gender = #{gender}
    ,	birth = #{birth}
	,	nickname = #{nickname}
    ,	password = password(#{password})
    , 	created = now()
    ,	token = #{token}

mapper.update:
UPDATE
		user

    
	,	nickname = #{nickname}
	
	
    ,	password = password(#{password})
	
	
	
    ,	token = #{token}
	

	WHERE
		email = #{email}
